import { Dispatch } from "redux";
import { AppThunk } from "../..";

import { apiList } from "../../actionNames";
import initDefaultAction, {
  APIResponseDetail,
} from "../../helper/default-action";
import initDefaultReducer from "../../helper/default-reducer";
import initialState from "../../helper/default-state";

export type IncomeCategoryType = {
  id: number;
  name: string;
}[];

const apiDetails = Object.freeze(apiList.icome.getIncomeCategory);

export default function getIncomeCategoryReducer(
  state = initialState,
  action: DefaultAction
): DefaultState<IncomeCategoryType> {
  const stateCopy = Object.assign({}, state);
  const actionName = apiDetails.actionName;

  return initDefaultReducer(actionName, action, stateCopy);
}

export const getIncomeCategoryAction =
  (water_scheme_slug): AppThunk<APIResponseDetail<IncomeCategoryType>> =>
  async (dispatch: Dispatch) => {
    return await initDefaultAction(apiDetails, dispatch, {
      disableSuccessToast: true,
      pathVariables: { water_scheme_slug },
    });
  };
